import React, { Component } from 'react';

class Text extends Component {
            
    render() {
        let name = 'سلطان';
        let job = 'مطور' ? 'مطور ' : 'مصمم';  
        return (
            <div>
                <p>مرحباً يا <span style={{color:'red'}}>{name}</span> وظيفتك هي <span style={{color:'#0987'}}>{job}</span></p>
            </div>
        );
    }
}

export default Text;
